{"cells":[{"cell_type":"code","source":["\r\n","from azureml.core.model import Model, Dataset\r\n","from azureml.core.run import Run, _OfflineRun\r\n","from azureml.core import Workspace\r\n","import argparse\r\n","\r\n","parser = argparse.ArgumentParser()\r\n","parser.add_argument(\"--model_name\", required=True)\r\n","parser.add_argument(\"--model_path\", required=True)\r\n","args = parser.parse_args()\r\n","\r\n","print(f\"model_name : {args.model_name}\")\r\n","print(f\"model_path: {args.model_path}\")\r\n","\r\n","run = Run.get_context()\r\n","ws = Workspace.from_config() if type(run) == _OfflineRun else run.experiment.workspace\r\n","\r\n","model = Model.register(workspace=ws,\r\n","                       model_path=args.model_path,\r\n","                       model_name=args.model_name)\r\n","\r\n","print(\"Registered version {0} of model {1}\".format(model.version, model.name))"],"outputs":[],"execution_count":null,"metadata":{}}],"metadata":{"kernelspec":{"name":"python3-azureml","language":"python","display_name":"Python 3.6 - AzureML"},"language_info":{"name":"python","version":"3.6.9","mimetype":"text/x-python","codemirror_mode":{"name":"ipython","version":3},"pygments_lexer":"ipython3","nbconvert_exporter":"python","file_extension":".py"},"kernel_info":{"name":"python3-azureml"},"nteract":{"version":"nteract-front-end@1.0.0"}},"nbformat":4,"nbformat_minor":2}